import React, { useState } from 'react';
import axios from 'axios';

function ChatWindow({ token, setToken }) {
  const [message, setMessage] = useState('');
  const [chatLog, setChatLog] = useState([]);

  const sendMessage = async () => {
    if (!message.trim()) return;
    setChatLog([...chatLog, { sender: 'user', text: message }]);

    try {
      const res = await axios.post(
        'http://localhost:5000/chat/message',
        { message },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setChatLog(prev => [...prev, { sender: 'bot', text: res.data.response }]);
      setMessage('');
    } catch (err) {
      if (err.response.status === 401) {
        alert('Session expired. Please login again.');
        setToken('');
      } else {
        alert('Error communicating with chatbot.');
      }
    }
  };

  return (
    <div>
      <h2>Chatbot</h2>
      <div style={{ minHeight: 200, border: '1px solid #ccc', padding: 10, marginBottom: 10 }}>
        {chatLog.map((chat, idx) => (
          <p key={idx} style={{ textAlign: chat.sender === 'user' ? 'right' : 'left' }}>
            <b>{chat.sender === 'user' ? 'You' : 'Bot'}:</b> {chat.text}
          </p>
        ))}
      </div>
      <input
        placeholder="Type your message"
        value={message}
        onChange={e => setMessage(e.target.value)}
        onKeyDown={e => e.key === 'Enter' && sendMessage()}
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
}

export default ChatWindow;
